package stringMethods;

public class ConcatInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="i love java and selenium";
		String s1=" i love PHP";
		System.out.println(s.concat(s1));//i love java and selenium i love PHP
		System.out.println(s+s1);////i love java and selenium i love PHP
		
	}

}
